var oVec2=Class.create({initialize:function(e,t){this.x=e,this.y=t},Add:function(e){this.x+=e.x,this.y+=e.y},Subtract:function(e){this.x-=e.x,this.y-=e.y},Multiply:function(e){this.x*=e,this.y*=e},Abs:function(){this.x=Math.abs(this.x),this.y=Math.abs(this.y)},Mag:function(){return Math.sqrt(this.x*this.x+this.y*this.y)},SetV:function(e){this.x=e.x,this.y=e.y},Set:function(e,t){this.x=e,this.y=t},SetMag:function(e){this.Normalize(),this.Multiply(e)},Normalize:function(){var e=this.Mag();if(e<Number.MIN_VALUE)return 0;var t=1/e;this.x*=t,this.y*=t}});oVec2.Multiply=function(e,t){return new oVec2(e.x*t,e.y*t)},oVec2.Add=function(e,t){return new oVec2(e.x+t.x,e.y+t.y)},oVec2.Subtract=function(e,t){return new oVec2(e.x-t.x,e.y-t.y)},oVec2.Dot=function(e,t){return e.x*t.x+e.y*t.y},oVec2.Cross=function(e,t){return e.x*t.y-e.y*t.x},oVec2.CrossZ=function(e,t){return new oVec2(-t.y*e,t.x*e)},oVec2.PerpendicularCW=function(e){return new oVec2(-e.y,e.x)},oVec2.PerpendicularCCW=function(e){return new oVec2(e.y,-e.x)},oVec2.Min=function(e,t){return e.Mag()<t.Mag()?e:t},oVec2.Normalize=function(e){var t=new oVec2(e.x,e.y);return t.Normalize(),t},oVec2.Max=function(e,t){return e.Mag()>=t.Mag()?e:t},oVec2.AreParallel=function(e,t){return 0==oVec2.Cross(e,t)?!0:!1};